<template>
  <img :class="this.class" :src="srcImg" @error="handleError" />
</template>

<script>
export default {
  props: {
    class: String,
    src: String,
  },
  data() {
    return {
      srcImg: String,
    };
  },
  mounted() {
    this.srcImg = this.src;
  },
  watch: {
    src: function (newVal, oldVal) {
      this.srcImg = newVal;
    },
  },
  methods: {
    handleError() {
      this.srcImg =
        "data:image/jpeg;base64,";
    },
  },
};
</script>

